mapper.insert:
INSERT INTO MEMBER (USER_NO
				  , USER_ID
				  , USER_PWD
				  , USER_NAME
				  , EMAIL
				  , PHONE
				  , BIRTH
				  , GENDER
				  , ADDRESS
				  , PROFILE)
VALUES (SEQ_MEMBER.NEXTVAL
	  , #{userId}
	  , #{userPwd}
	  , #{userName}
	  , #{email}
	  , #{phone}
	  , #{birth}
	  , #{gender}
	  , #{address}
	  , #{profile})

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID = #{userId}

SELECT *
FROM MEMBER
WHERE USER_STATUS = 'Y'
AND USER_ID = #{userId}
AND USER_PWD = #{userPwd}

SELECT COUNT(*)
FROM MEMBER
WHERE USER_NAME = #{userName}
AND EMAIL = #{email}
AND USER_STATUS = 'Y'

SELECT USER_ID
FROM MEMBER
WHERE USER_NAME = #{userName}
AND EMAIL = #{email}
AND USER_STATUS = 'Y'

SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID = #{userId}
AND USER_NAME = #{userName}
AND EMAIL = #{email}
AND USER_STATUS = 'Y'

SELECT USER_PWD
FROM MEMBER
WHERE USER_ID = #{userId}
AND USER_NAME = #{userName}
AND EMAIL = #{email}
AND USER_STATUS = 'Y'

mapper.update:
UPDATE MEMBER
SET BIRTH = #{birth}
  , ADDRESS = #{address}
  , PROFILE = #{profile}
WHERE USER_NO = #{userNo}
